    graph[x][y] = graph[y][x] = 1

def dfs(v, c=1, visited=[]):
    visited.append(v)
    if c == -1:
        return not any(visited)
    if c == 1:
        return not any(visited) or dfs(visited[-1], -c, visited[:-1])
    for n in range(N):
        if graph[v][n] and visited[n] == -1:
            if dfs(n, -c, visited[:visited.index(v)+1] + [n]):
                return False
    visited.pop()
    return True

if dfs(0, 1):
    x = sum(v + 1 for v in [1] + [0]*(N-1)) // 2
    print((x * (N - x) - M))
else:
    print((N * (N - 1) // 2 - M))
