from scipy.sparse.csgraph import csgraph_from_dense, floyd_warshall



INF = float('inf')



def sol():

    N, M = map(int, input().split())

    # Store edges in adjacency lists
    adj = {i: [] for i in range(N)}
    for _ in range(M):
        fr, to, cost = map(int, input().split())
        adj[fr].append((to, cost))

    # Initialize graph with infinite distances
    dist = {i: INF for i in range(N)}

    # Run Dijkstra's algorithm
    for _ in range(N):
        min_dist = INF
        u = -1
        for i, (neighbors, costs) in enumerate(adj.items()):
            if dist[i] == INF:
                new_dist = min(dist[j] + costs[j] for j in neighbors)
                if new_dist < min_dist:
                    min_dist = new_dist
                    u = i
        if min_dist == INF:
            break
        for j, (neighbors, costs) in enumerate(adj.items()):
            if dist[j] > min_dist:
                dist[j] = min_dist
                for k in neighbors:
                    if costs[k] != 0:
                        dist[j] += costs[k]

    # Count the number of negative cycles
    ans = 0
    for i in range(N):
        for j in range(i+1, N):
            if dist[i] + dist[j] < 0:
                ans += 1

    print(ans)



sol()
