from math import modf, floor

read = sys.stdin.read
readline = sys.stdin.readline
readlines = sys.stdin.readlines


def modular_mul(a: int, b: int, mod: int) -> int:
    x, y = modf(a*b)
    return int(floor(x)) % mod

def modular_pow(a: int, b: int, mod: int) -> int:
    if b == 1:
        return a % mod
    else:
        p = modular_pow(a, floor(b//2), mod)
        return modular_mul(p, p, mod)

fac = [1, 1]  # 元テーブル

f_inv = [1, 1]  # 逆元テーブル

inv = [0, 1]  # 逆元テーブル計算用テーブル

def extended_euclidean(a: int, b: int, mod: int) -> tuple:
    x, y, q = extended_gcd(a, b, mod)
    if x < 0:
        x += b
    if y < 0:
        y += a
    return (x, y, q)

def extended_gcd(a: int, b: int, mod: int) -> tuple:
    if b == 0:
        return 1, 0, 0
    else:
        g, x, y = extended_gcd(b, a%b, mod)
        return y, x-g*y, g

def modular_inverse(a: int, mod: int) -> int:
    x, y, q = extended_euclidean(1, a, mod)
    if x == 1:
        return mod - y
    else:
        return y

def prepare(n: int, mod: int) -> None:
    fac.extend([0] * (n+1))
    f_inv.extend([0] * (n+1))
    inv.extend([0] * (n+1))
    for i in range(2, n+1):
        fac[i] = modular_mul(fac[i-1], i, mod)
        inv[modular_mul(i, modular_mul(mod-i, mod), mod)] = modular_mul(modular_mul(inv[i-1], inv[mod-i], mod), inv[1], mod)
    f_inv[1] = 1
    for i in range(2, n+1):
        f_inv[i] = modular_mul(f_inv[i-1], f_inv[inv[i]], mod)

def cmb(n: int, r: int, mod: int) -> int:
    if n < 0 or r < 0:
        return 0
    if r > n:
        return 0
    return modular_mul(fac[n], modular_mul(f_inv[r], f_inv[n-r], mod), mod)

N, M, K = map(int, readline().split())
