#include <stdlib.h>
#include <string.h>

char* escape_html(char* input) {
  // Remove backslashes
  input = strreplace(input, "\\", "");

  // Escape all HTML special characters
  input = strreplace(input, "&", "&amp;");
  input = strreplace(input, "'", "&apos;");
  input = strreplace(input, "<", "&lt;");
  input = strreplace(input, ">", "&gt;");
  input = strreplace(input, '"', "&quot;");

  return input;
}

