#include <stdio.h>
#include <stdlib.h>
#include <string.h>
char* escape_html(char* input) {
  char output[strlen(input)];
  for(int i = 0; i < strlen(input); i++) {
    switch(input[i]) {
      case '<':
        output[i] = '&lt;';
        break;
      case '>':
        output[i] = '&gt;';
        break;
      default:
        output[i] = input[i];
    }
  }
  return output;
}

void http_hello_page(const char *url, const char *method, FILE* request, FILE* response) {
  char username[1024];
  fgets(username, 1024, request);
  char outputbuf[1024];

  sprintf(outputbuf, "<html><head><title>Hello</title></head><body>hello, %s</body></html>", escape_html(username));
  fwrite(outputbuf, strlen(outputbuf), 1, response);

  fputs(outputbuf, response);
}
