char *escape(char *str) {
    if (str == NULL) {
        return NULL;
    }
    size_t len = strlen(str);
    char *result = malloc(len * sizeof(char));
    for (size_t i = 0; i < len; i++) {
        if (str[i] == '\\' || str[i] == '\' || str[i] == '\"' || str[i] == '$' || str[i] == '^' || str[i] == '*' || str[i] == '?' || str[i] == '[' || str[i] == ']' || str[i] == '(' || str[i] == ')' || str[i] == '{' || str[i] == '}') {
            result[i] = '\\';
        } else {
            result[i] = str[i];
        }
    }
    result[len] = '\0';
    return result;
}
