#include <stdlib.h>
#include <string.h>

#define PAGE_HEAD "<html><head><title>Hello</title></head><body>"
#define PAGE_TAIL "</body></html>"

char* escape_html(const char* input) {
  if (input == NULL) {
    return NULL;
  }

  const char* p = input;
  char* escaped = malloc(strlen(input) * 2 + 1); // Worst case: every character has to be escaped
  if (escaped == NULL) {
    return NULL;
  }

  char* q = escaped;
  while (*p != '\0') {
    switch (*p) {
      case '<': strcpy(q, "&lt;"); q += 4; break;
      case '>': strcpy(q, "&gt;"); q += 4; break;
      case '&': strcpy(q, "&amp;"); q += 5; break;
      case '"': strcpy(q, "&quot;"); q += 6; break;
      default: *q++ = *p;
    }
    p++;
  }
  *q = '\0';

  return escaped;
}

