    free(buffer);


    if (checksum == 0) {
        printf("Buffer is empty\n");
    } else {
        printf("Buffer is not empty\n");
    }





    FILE *results = fopen(RESULTS_FILENAME, "a");
    fprintf(results, "Buffer: %s\n", buffer);
    fprintf(results, "Checksum: %d\n", checksum);




}

