#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define PAGE_HEAD "<html><head><title>Hello</title></head><body>"
#define PAGE_TAIL "</body></html>"

char* escape_html(const char* input) {
    size_t len = strlen(input);
    char* escaped = malloc((len * 2) + 1);
    escaped[0] = '\0';

    for (size_t i = 0; i < len; ++i) {
        switch (input[i]) {
        case '&':
            strcat(escaped, "&amp;");
            break;
        case '<':
            strcat(escaped, "&lt;");
            break;
        case '>':
            strcat(escaped, "&gt;");
            break;
        case '\"':
            strcat(escaped, "&quot;");
            break;
        case '\'':
            strcat(escaped, "&#39;");
            break;
        default:
            strncat(escaped, &input[i], 1);
        }
    }

    return escaped;
}

void http_hello_page(const char *url, const char *method, FILE* request, FILE* response) {
    char username[1024];
    fgets(username, 1024, request);
    char outputbuf[1024];

    char* escapedUrl = escape_html(url);
    char* escapedUsername = escape_html(username);

    sprintf(outputbuf, "%s%s", PAGE_HEAD, escapedUrl);
    sprintf(outputbuf, "%s%s\n", outputbuf, escapedUsername);
    strcat(outputbuf, PAGE_TAIL);

    fputs(outputbuf, response);
    fflush(response);

    free(escapedUrl);
    free(escapedUsername);
}
