  char* result = (char*) malloc(sizeof(char) * (strlen(input) + 1));
  int i;
  for (i = 0; i < strlen(input); i++) {
    if (input[i] == '<') {
      result[i] = '&';
      result[i+1] = 'l';
      result[i+2] = 't';
      result[i+3] = ';';
      i += 3;
    } else if (input[i] == '>') {
