char* escape_html(char* input) {
  char *safe_input = malloc(sizeof(char) * strlen(input) * 2 + 1);
  memset(safe_input, 0, sizeof(char) * strlen(input) * 2 + 1);
  char c;
  int i = 0;
  while ((c = input[i++]) != '\0') {
    switch (c) {
      case '<':
        strcat(safe_input, "&lt;");
        break;
      case '>':
        strcat(safe_input, "&gt;");
        break;
      case '&':
        strcat(safe_input, "&amp;");
        break;
      case '"':
        strcat(safe_input, "&quot;");
        break;
      default:
        safe_input[i-1] = c;
    }
  }
  return safe_input;
}

