    printf("fcntl mode: %d\n", mode);
    int res = fchmod(fd, mode | S_IRUSR | S_IWUSR);
    if (res == 0) {
    	printf("Could not change file mode for important_config file, exiting.\n");
    	return 0;
    }
    mode = fcntl(fd, F_GETFL);
    printf("fcntl mode: %d\n", mode);
    res = close(fd);
    if (res != 0) {
    	return 0;
    }

    return 0;

}

