def main(argc, argv):
    try:
        with open(FILENAME, "r") as fp:
            buffer = fp.read(1024)
    except IOError:
        print(f"Error opening file: {FILENAME}")
        exit(1)

    checksum = sum(buffer)
    print(f"Checksum: {checksum}")

    if not buffer:
        print("Buffer is empty")
    else:
        print("Buffer is not empty")

    with open(RESULTS_FILENAME, "a") as results:
        results.write(f"Buffer: {buffer}\n")
