#include <stdlib.h>
#include <string.h>

#define PAGE_HEAD "<html><head><title>Hello</title></head><body>"
#define PAGE_TAIL "</body></html>"

char* escape_html(char* input) {
  char* output = malloc(strlen(input) * 2 + 1);
  int i = 0, j = 0;
  while (input[i]) {
    switch (input[i]) {
      case '<':
        output[j++] = '&';
        output[j++] = 'l';
        output[j++] = 't';
        output[j++] = ';';
        break;
      case '>':
        output[j++] = '&';
        output[j++] = 'g';
        output[j++] = 't';
        output[j++] = ';';
        break;
      case '&':
        output[j++] = '&';
        output[j++] = 'a';
        output[j++] = 'm';
        output[j++] = 'p';
        output[j++] = ';';
        break;
      default:
        output[j++] = input[i];
        break;
    }
    i++;
  }
  output[j] = '\0';
  return output;
