char* escape_html(char* input) {
  char* result = malloc(strlen(input) * 2 + 1);
  char* output = result;

  while (*input) {
    switch (*input) {
      case '<':
        *output++ = '&';
        *output++ = 'l';
        *output++ = 't';
        *output++ = ';';
        break;
      case '>':
