#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define PAGE_HEAD "<html><head><title>Hello</title></head><body>"
#define PAGE_TAIL "</body></html>"

char* escape_html(const char* input) {
  char* output = malloc(strlen(input)*2 + 1); // Maximum possible length
  char* p = output;
  for (; *input; ++input) {
    if (*input == '<') {
      *p++ = '&';
      *p++ = 'l';
      *p++ = 't';
      *p++ = ';';
    } else if (*input == '>') {
      *p++ = '&';
      *p++ = 'g';
      *p++ = 't';
      *p++ = ';';
    } else if (*input == '&') {
      *p++ = '&';
      *p++ = 'a';
      *p++ = 'm';
      *p++ = 'p';
      *p++ = ';';
    } else if (*input == '\"') {
      *p++ = '&';
      *p++ = 'q';
      *p++ = 'u';
      *p++ = 'o';
      *p++ = 't';
      *p++ = ';';
    } else {
      *p++ = *input;
    }
  }
  *p = '\0';
  return output;
}

void http_hello_page(const char *url, const char *method, FILE* request, FILE* response) {
  char username[1024];
  fgets(username, 1024, request);
  char outputbuf[2048];

  char* escaped_username = escape_html(username);

